    /*
     * Editing a tbl_ourpartner
     */
    function edit($id_ourpartner)
    {   
        // check if the tbl_ourpartner exists before trying to edit it
        $data['tbl_ourpartner'] = $this->Tbl_ourpartner_model->get_tbl_ourpartner($id_ourpartner);
        
        if(isset($data['tbl_ourpartner']['id_ourpartner']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {

                $config['upload_path']          = './uploads/';
                $config['allowed_types']        = 'gif|jpg|png';
                $config['max_size']             = 100;
                $config['max_width']            = 1024;
                $config['max_height']           = 768;

                $this->load->library('upload', $config);

                if (!$this->upload->do_upload('ourpartner_img'))
                {
                    $params = array(
                        'ourpartner_name' => $this->input->post('ourpartner_name'),
                        'ourpartner_desc_id' => $this->input->post('ourpartner_desc_id'),
                        'ourpartner_desc_en' => $this->input->post('ourpartner_desc_en'),
                    );
                    
                    $this->Tbl_ourpartner_model->update_tbl_ourpartner($id_ourpartner,$params); 
                    redirect('admin/OurPartner');
