                        'ourpartner_name' => $this->input->post('ourpartner_name'),
                        'ourpartner_img' => $this->upload->data('file_name'),
                        'ourpartner_desc_id' => $this->input->post('ourpartner_desc_id'),
                        'ourpartner_desc_en' => $this->input->post('ourpartner_desc_en'),
                    );
                    
                    $this->Tbl_ourpartner_model->update_tbl_ourpartner($id_ourpartner,$params);  
                    redirect('admin/OurPartner');
                }
            }
            else
            {
                $data = array(
                    "site" => $_SERVER['SERVER_NAME'],
                    "page" => "OurPartner",
                    "tbl_ourpartner" => $this->Tbl_ourpartner_model->get_tbl_ourpartner($id_ourpartner)
                );
                
                $this->load->view('layout/admin/header', $data);
                $this->load->view('edit', $data);
                $this->load->view('layout/admin/footer');
            }
        }
        else
            show_error('The tbl_ourpartner you are trying to edit does not exist.');
    } 

    /*
     * Deleting tbl_ourpartner
     */
    function remove($id_ourpartner)
    {
        $tbl_ourpartner = $this->Tbl_ourpartner_model->get_tbl_ourpartner($id_ourpartner);

        // check if the tbl_ourpartner exists before trying to delete it
        if(isset($tbl_ourpartner['id_ourpartner']))
        {
            unlink('./uploads/'.$tbl_ourpartner['ourpartner_img']);
            $this->Tbl_ourpartner_model->delete_tbl_ourpartner($id_ourpartner);
            redirect('admin/OurPartner');
        }
        else
            show_error('The tbl_ourpartner you are trying to delete does not exist.');
    }
    
